mapper.update:
UPDATE
	infrcodegroup
SET 
	ifcgName = #{ifcgName}
WHERE 1=1
	AND ifcgSeq = #{ifcgSeq}

mapper.select:
select a.ifcdSeq ,a.ifcdName from shop.infrcode a where 1=1 and ifcdDelNy=0 AND ifcgSeq =#{shIfcgSeq}
AND ifcgName LIKE concat('%',#{shValue},'%') AND ifcgNameEng LIKE concat('%',#{shValue},'%')
limit #{startRnumForMysql},#{rowNumToShow}

mapper.update:
