mapper.update:
UPDATE
	infrmember
SET 
	ifmmPassword = #{ifmmPassword}
	,ifmmDob = #{ifmmDob}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE
	infrmemberPhone
SET 
	ifmpNumber = #{ifmpNumber}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE
	infrmemberAddress
SET 
	ifmaAddress1 = #{ifmaAddress1}
	,ifmaAddress2 = #{ifmaAddress2}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE
	infrmemberEmail
SET 
	ifmeEmailFull = #{ifmeEmailFull}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
